export default function DataLoader() {
  const [data, setData] = useState([]);

  useEffect( () => {

     function getData () {
      fetch("http://www.lufcnews.co.uk/wp-json/wp/v2/posts")
      .then(response => response.json())
      .then(data => setData(data));
      console.log(data);
    };

    getData();
  }, [data]);

  const posts = (
    data.map(d => (
      <li key={d.id}>{d.title}</li>
    ))
  );

  if (data.length > 0 ) {
    return (
      <div>
        <ul>
          {/* {posts} */}
        </ul>
      </div>
    );
  }
  return <h1>hello</h1>
}
