import React, { useState, useEffect } from "react";

export default function DataLoader() {
  const [data, setData] = useState([]);

  useEffect( () => {

     function getData () {
      fetch("http://www.lufcnews.co.uk/wp-json/wp/v2/posts?_embed")
      .then(response => response.json())
      .then(data => setData(data));
      console.log(data);
    };

    getData();
  }, [data]);

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      flexDirection: 'row',
      flexWrap: 'wrap',
    },
    item :{
      flex: 0.5, //why this doesnt work???
      // width: 150, //using fixed item width instead of flex: 0.5 works
      height: 100,
      padding: 10,
      backgroundColor: 'red',
      // flexGrow: 1,
      // flexShrink: 0,
    }
  });

  const posts = (
    data.map(d => (
      <div key={d.id} style={styles.container}>
              <li style={styles.item}>{d.title.rendered}</li>
      <img src={d._embedded['wp:featuredmedia']['0'].source_url}></img>
      </div>
    ))
  );

    return (
      <div>
        <ul>
       { posts }
        </ul>
      </div>
    );
}
