import React, { useState, useEffect } from "react";

export default function DataLoader() {
  const [data, setData] = useState([]);

  useEffect( () => {

     function getData () {
      fetch("http://www.lufcnews.co.uk/wp-json/wp/v2/posts_embed")
      .then(response => response.json())
      .then(data => setData(data));
      console.log(data);
    };

    getData();
  }, [data]);

  const posts = (
    data.map(d => (
      <li key={d.id}>{d.title.rendered}</li>
    ))
  );

    return (
      <div>
        <ul>
       { posts }
        </ul>
      </div>
    );
}
