import React, { useState, useEffect } from "react";

export default function DataLoader() {
  const [data, setData] = useState([]);

  function getData () {
    fetch("http://www.lufcnews.co.uk/wp-json/wp/v2/posts")
    .then(response => response.json())
    .then(data => setData(data));
    console.log(data);
  };

  useEffect(async () => {
    await getData();
  }, [data]);

  return (
    <div>
      <ul>
        {/* {data.array.map(d => (
          <li>{d.title}</li>
        ))} */}
      </ul>
    </div>
  );
}
