import React, { useState, useEffect } from "react";
import "./App.scss";
import { Grid, GridRow, GridColumn, Image } from "semantic-ui-react";

export default function DataLoader() {
  const [data, setData] = useState([]);

  useEffect( () => {

     function getData () {
      fetch("http://www.lufcnews.co.uk/wp-json/wp/v2/posts?_embed")
      .then(response => response.json())
      .then(data => setData(data));
      console.log(data);
    };

    getData();
  }, [data]);

  const posts = (
    data.map(d => (
        <GridColumn width={4}>
          <Image width={7} src={d._embedded['wp:featuredmedia']['0'].source_url}/>
          <a>{d.title.rendered}</a>
        </GridColumn>
    ))
  );

    return (
      <Grid>
        <GridRow>

       { posts }
        </GridRow>
      </Grid>
    );
}
