import React, { useState, useEffect } from "react";
import "./App.scss";
import { Grid, GridRow, GridColumn, Image, Segment } from "semantic-ui-react";

export default function DataLoader() {
  const [data, setData] = useState([]);


  // useEffect() is a function that handles actions when something affects your component
  // So we want to fetch our API when certain things affect our component
  useEffect( () => {
  // Here we define the function getData which fetches our API data
     function getData () {
      // Select API endpoint
      fetch("http://www.lufcnews.co.uk/wp-json/wp/v2/posts?_embed")
      .then(response => response.json())
      .then(data => setData(data));
      console.log(data);
    };

    getData();
  }, [data]);

  const posts = (
    data.map(d => (
        <GridColumn width={4}>
          <Image width={700} src={d._embedded['wp:featuredmedia']['0'].source_url}/>
          <a>{d.title.rendered}</a>
        </GridColumn>
    ))
  );

    return (
      <Segment>
        <Grid columns={4}>
        <GridRow>
            { posts }
        </GridRow>
      </Grid>
      </Segment>
    );
}
